import axios from 'axios';
import React from 'react';

class MyWords extends React.Component{
    state = {
        word : "",
        meaning : "",
        words : []
    }
    componentDidMount(){
        axios.get('/listwords')
        .then(response => {
            // console.log(response.data)
            this.setState({
                words : response.data
            })
        })
    }
    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        });
    }
    handleSubmit = event => {
        event.preventDefault();
        var word = this.state.word
        var meaning = this.state.meaning
        axios.post('/addword', {word, meaning})
        .then(response => {
            console.log(response)
            this.setState({
                word : "",
                meaning : ""
            })
        })
    }
    handleDelete = (argsword) => {
        axios.post('/deleteword', {argsword})
        .then(response => {
            console.log(response)
        })
    }
    render(){
        return (
            <div>
                <div>List of My words and their meanings</div>
                <div>
                    {this.state.words.map(word => 
                        <div key={word.question}>
                            {word.question} means {word.correct_answer}
                            <button onClick={this.handleDelete(word.question)}>x</button>
                        </div>
                        )
                    }
                </div>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <input 
                            name="word"
                            value={this.state.word}
                            placeholder="Enter the word"
                            onChange={this.handleChange}
                        />
                    </div>
                    <div>
                        <input 
                            name="meaning"
                            value={this.state.meaning}
                            placeholder="Enter the meaning of word"
                            onChange={this.handleChange}
                        />
                    </div>
                    <button type="submit">Add to my list</button>
                </form>
            </div>
        );
    }
}

export default MyWords;
