import axios from 'axios';
import React from 'react';

class MyWords extends React.Component{
    state = {
        word : "",
        meaning : "",
        words : []
    }
    componentDidMount(){
        axios.get('/listwords')
        .then(response => {
            console.log(response)
            this.setState({
                words : response.listOfWords
            })
        })
    }
    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        });
    }

    render(){
        return (
            <div>
                <div>List of My words and their meanings</div>
                <div>
                    {this.state.words.map(word => 
                        <div key={word.word}>
                            {word.word} means {word.meaning}
                            <button onClick={() => this.handleDelete(word.word)}>x</button>
                        </div>
                        )
                    }
                </div>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <input 
                            name="word"
                            value={this.state.word}
                            placeholder="Enter the word"
                            onChange={this.handleChange}
                        />
                    </div>
                    <div>
                        <input 
                            name="meaning"
                            value={this.state.meaning}
                            placeholder="Enter the meaning of word"
                            onChange={this.handleChange}
                        />
                    </div>
                    <button type="submit">Add to my list</button>
                </form>
            </div>
        );
    }
}

export default MyWords;
