class Quiz extends React.Component{
  state = {
    curr_question : 0,
    words : []
  }
  componentDidMount(){
    axios.get('/listwords')
      .then(response => {
        // console.log(response.data)
        this.setState({
          words : response.data
        })
      })
  }
  render(){
    return (
      <div>
        <div>
          {this.state.words[0].question}
          <div>
            {this.state.words[0].options.map(option => (
              <button onClick={() => {
                console.log({option})
              }}>{option}</button>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

