import React from 'react';
import DictionaryAPI from './components/DictionaryAPI';
import Quiz from './components/Quiz';
import MyWords from './components/MyWords';
import Login from './components/Login';
import Registration from './components/Registration';
import { Switch, Route, Link } from 'react-router-dom';




function Navbar(){
  return (
    <div>
      <Link to="/">MyWords</Link>
      <Link to="/quiz">Quiz</Link>
      <Link to="/search">Search</Link>
    </div>
  );
}

const PrivateRouteRegister = (token, setToken) => {
  return (
    token ? 
    <div>
      <Login setToken={setToken}/>
    </div> 
    : null
  );
}

const PrivateRoute = (token,setToken) => {
  return (
    token ? 
    <div>
      <Route />
    </div> 
    : 
    <div>
      <Login setToken={setToken} />
    </div>
  );
}

function setToken(usertoken){
  localStorage.setItem('token', JSON.stringify(usertoken));
}

function getToken(){
  const tokenString = localStorage.getItem('token');
  const userToken = JSON.parse(tokenString);
  return userToken?.token
}

class App extends React.Component{
  state = {
    token : getToken(),
    isLoggedIn : false,
  }
  render(){
    return (
      <div>
        <Navbar />
        <Switch>
          <Login setToken={setToken} token={this.state.token}/>
          <Registration setToken={setToken} token={this.state.token}/>
          <PrivateRoute 
            path='/' 
            exact 
            token={this.state.token}
            setToken={setToken}
            render={(props) => <MyWords/>}
          />  
          <PrivateRoute 
            path='/quiz'
            component={Quiz}
            token={this.state.token}
            setToken={setToken}
          />
          <Route 
            path='/search' 
            component={DictionaryAPI}
            token={this.state.token}
            setToken={setToken}
          />
        </Switch>
      </div>
    );
  }
}



export default App;
