var random_words = require('random-words');

export let listOfWords = [

]

export const deleteWords = (passedword) => {
  listOfWords.filter(word => word.word !== passedword)
}

export let quizset = [];



let loadQuestions = () => {
  listOfWords.map(word => {
    var options = random_words(3) + "," +  word.word
    options = options.split(",")
    quizset.push({
      options : options,
      question : word.meaning,
      correct_answer : word.word
    })
  })
}

loadQuestions();
