import React from 'react';

const quizset = [
  {
    question : "1+1 = ",
    options : [2, 3, 4, 5],
    correct_answer : 2
  },
  {
    question : "1+2=",
    options : [2,3,4,5],
    correct_answer : 3
  },
  {
    question : "2+3=",
    options : [3,4,5,6],
    correct_answer : 5
  }
];

class App extends React.Component{
  state = {
    current_question : 0,
    option_marked : null
  }
  nextQuestion = () => {
    let current_Index = this.state.current_question
    let nextIndex = current_Index + 1
    if(current_Index === quizset.length - 1){
      nextIndex = -1
    }
    this.setState({
      current_question : nextIndex,
      option_marked : null
    });
  }
  render(){
    return (
     <div>
       <div>Hello world!!</div>
       {this.state.current_question === -1 ?
       null : 
       <div>
         {quizset[this.state.current_question].question}
         {quizset[this.state.current_question].options.map(option => (
          <div key={option} onClick={() => this.setState({
            option_marked : option
          })}>
            {option}
          </div>
         ))}
         {this.state.option_marked === null ? null : 
          <div>
            {this.state.option_marked === quizset[this.state.current_question].correct_answer ? 
              <div style={{color : "green"}}>Correct</div> : 
              <div style={{color : "red"}}>Wrong</div>
            }
          </div>
         }
         <button onClick={this.nextQuestion}>next question</button>
       </div>  
        }
       <div>bye World!!</div>
     </div>
    );
  }
}



export default App;
