import React from 'react';
import data from './data';
var random_words = require('random-words');


var quizset = [
    {
      question : "2+3=",
      options : [3,4,5,6],
      correct_answer : 5
    }
  ];



const loadQuestions = () => {
  data.listOfWords.map(word => {
    var options = random_words(3) + "," +  word.word
    options = options.split(",")
    quizset.push({
      options : options,
      question : word.meaning,
      correct_answer : word.word
    })
  })
}


class Quiz extends React.Component{
    state = {
        current_question : 0,
        option_marked : null
      }
      componentDidMount(){
        console.log(data.listOfWords)
        loadQuestions()
        console.log(quizset)
      }
      nextQuestion = () => {
        let current_Index = this.state.current_question
        let nextIndex = current_Index + 1
        if(current_Index === quizset.length - 1){
          nextIndex = -1
        }
        this.setState({
          current_question : nextIndex,
          option_marked : null
        });
      }
      render(){
        return (
         <div>
           {this.state.current_question === -1 ?
           null : 
           <div>
             The question is : 
             {quizset[this.state.current_question].question}
             {quizset[this.state.current_question].options.map(option => ( 
              <div style={{cursor : "pointer"}} key={option} onClick={() => this.setState({
                option_marked : option
              })}>
                {option}
              </div>
             ))}
             {this.state.option_marked === null ? null : 
              <div>
                {this.state.option_marked === quizset[this.state.current_question].correct_answer ? 
                  <div style={{color : "green"}}>Correct</div> : 
                  <div style={{color : "red"}}>Wrong</div>
                }
              </div>
             }
             <button onClick={this.nextQuestion}>next question</button>
           </div>  
            }
           <div>bye World!!</div>
         </div>
        );
      }
}

export default Quiz;
