import React from 'react';
import axios from 'axios';



class Quiz extends React.Component{
  state = {
    words : [],
    quizQuestions : [],
    curr_q : 0,
    clicked : false,
  }
  componentDidMount(){
    axios.get('/listwords').then((res) => {
      // console.log(res);
      this.setState({
        words : res.data
      });
    })
  }
  render(){
    if(this.state.words.length <= 0){
      return <div>No words added yet...</div>
    }
    return (
      <div>
        {this.state.words[this.state.curr_q]?.question}
        <div>
          <div>
            <input 
              type="radio" 
              value={this.state.words[this.state.curr_q]?.options[0]} 
              checked="false"
            />
          </div>
          <div>
            <input 
              type="radio" 
              value={this.state.words[this.state.curr_q]?.options[1]} 
              checked="false"
            />
          </div>
          <div>
            <input 
              type="radio" 
              value={this.state.words[this.state.curr_q]?.options[2]} 
              checked="false"
            />
          </div>
          <div>
            <input 
              type="radio" 
              value={this.state.words[this.state.curr_q]?.options[3]} 
              checked="false"
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Quiz;
