import React from 'react';

const quizset = [
  {
    question : "1+1 = ",
    options : [2, 3, 4, 5],
    correct_answer : 2
  },
  {
    question : "1+2=",
    options : [2,3,4,5],
    correct_answer : 3
  },
  {
    question : "2+3=",
    options : [3,4,5,6],
    correct_answer : 5
  }
];

class App extends React.Component{
  state = {
    current_question : 0,
  }
  nextQuestion = () => {
    let current_Index = this.state.current_question
    let nextIndex = current_Index + 1
    if(current_Index === quizset.length - 1){
      nextIndex = 0
    }
    this.setState({
      current_question : nextIndex
    });
  }
  handleOptionClick = (event) => {
    console.log(event.target.value);
  }
  render(){
    const listitems = quizset.map((obj) => 
      <div key={obj.correct_answer}>
        <div>{obj.question}</div>
        {obj.options.map((option) => 
          <div key={option}>{option}</div>
        )}
      </div>
    );
    return (
     <div>
       <div>Hello world!!</div>
       <div>
         {quizset[this.state.current_question].question}
         {quizset[this.state.current_question].options.map(option => (
          <div key={option} onClick={this.handleOptionClick}>
            {option}
          </div>
         ))}
         <button onClick={this.nextQuestion}>next question</button>
       </div>
       <div>bye World!!</div>
     </div>
    );
  }
}



export default App;
