import React from 'react';
import data from './data';

export default class DictionaryAPI extends React.Component{
    state = {
        word : "",
        meaning : "",
        loading : true
    }
    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        });
    }
    async loadData(){
        var url = "https://api.dictionaryapi.dev/api/v2/entries/en/";
        url = url + this.state.word
        const response = await fetch(url);
        const data = await response.json();
        this.setState({
            loading : false,
            word : data[0].word,
            meaning : data[0].meanings[0].definitions[0].definition
        });
        console.log(this.state.word);
        console.log(this.state.meaning);
    }
    handleSubmit = (event) => {
        event.preventDefault();
        this.loadData()
    }
    addWords = event => {
        data.listOfWords.push({
            word : this.state.word,
            meaning : this.state.meaning
        })
        console.log(data.listOfWords)
    }
    render(){
        return (
           <div>
                <form onSubmit={this.handleSubmit}>
                    <input 
                        placeholder="Enter a word..."
                        name="word"
                        value={this.state.word}
                        onChange={this.handleChange}
                    />
                    {this.state.loading ? null : 
                        <div>
                            {this.state.meaning}
                        </div>
                    }
                    <div>
                        <button type="submit">Search</button>
                    </div>
                </form>
                <div>
                    <button onClick={this.addWords}>Add to my list</button>
                </div>
           </div>
        );
    }
}
