import React from 'react';

const quizset = [
  {
    question : "1+1 = ",
    options : [2, 3, 4, 5],
    correct_answer : 2
  },
  {
    question : "1+2=",
    options : [2,3,4,5],
    correct_answer : 3
  },
  {
    question : "2+3=",
    options : [3,4,5,6],
    correct_answer : 5
  }
];

class App extends React.Component{
  state = {
    current_question : 0,
    questions_answered : 0,
    correct_answers : 0
  }
  nextQuestion = () => {
    this.setState({
      current_question : this.state.current_question + 1,
      questions_answered : this.state.questions_answered + 1
    });
  }
  handleOptionClick = (event) => {
    console.log(event.target.value);
  }
  render(){
    const listitems = quizset.map((obj) => 
      <div key={obj.correct_answer}>
        <div>{obj.question}</div>
        {obj.options.map((option) => 
          <div key={option}>{option}</div>
        )}
      </div>
    );
    return (
     <div>
       <div>Hello world!!</div>
       {this.state.questions_answered > 3 ? 
       null : 
       <div>
         {quizset[this.state.current_question].question}
         {quizset[this.state.current_question].options.map((option) => {
           <div key={option} onClick={this.handleOptionClick}>{option}</div>
         })}
       </div>
        }
       <button onClick={this.nextQuestion}>Next question</button>
       <div>bye World!!</div>
     </div>
    );
  }
}



export default App;
