import axios from 'axios';
import React from 'react';

class MyWords extends React.Component{
    state = {
        word : "",
        meaning : "",
        words : listOfWords
    }
    componentDidMount(){
        axios.get('/listwords')
        .then(response => {
            console.log(response)
        })
    }
    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        });
    }
    handleSubmit = event => {
        event.preventDefault();
        listOfWords.push({
            word : this.state.word,
            meaning : this.state.meaning
        });
        this.setState({
            words : listOfWords,
            word : "",
            meaning : ""
        });
        console.log(listOfWords)
    }
    handleDelete = (passedword) => {
        this.setState({
            words : this.state.words.filter(word => word.word !== passedword)
        });
        listOfWords.splice(0,listOfWords.length);
        this.state.words.map(word => listOfWords.push({
            word : word.word,
            meaning : word.meaning
        }))
        console.log(listOfWords);
    }
    render(){
        return (
            <div>
                <div>List of My words and their meanings</div>
                <div>
                    {this.state.words.map(word => 
                        <div key={word.word}>
                            {word.word} means {word.meaning}
                            <button onClick={() => this.handleDelete(word.word)}>x</button>
                        </div>
                        )
                    }
                </div>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <input 
                            name="word"
                            value={this.state.word}
                            placeholder="Enter the word"
                            onChange={this.handleChange}
                        />
                    </div>
                    <div>
                        <input 
                            name="meaning"
                            value={this.state.meaning}
                            placeholder="Enter the meaning of word"
                            onChange={this.handleChange}
                        />
                    </div>
                    <button type="submit">Add to my list</button>
                </form>
            </div>
        );
    }
}

export default MyWords;
